import React, { Component } from 'react';
import { Col, Row, Container } from 'reactstrap';
import Header from '../header';
import RandomChar from '../randomChar';
import CharacterPage from '../CharacterPage'
import styled from 'styled-components'
import ErrorMessage from '../ErrorMessage'
import CharDetails from '../charDetails'
import ItemList from '../itemList'
import gotServices from '../../Services'


import './button.css'


// import gotServices from '../../Services'
const Button1 = styled.button`
background-color: rgb(43, 97, 199);
border: none;
color: white;
padding: 2px 2px;
text-align: center;
text-decoration: none;
display: inline-block;
width: 180px;
font-size: 30px;
`;
const Div1 = styled.div`
padding-top:20px
padding-bottom:20px
width:200px`




export default class App extends Component {
    state = {
        Random: true,
        error: false

    }
    gotServices = new gotServices()
    name = 'hide'

    componentDidCatch() {
        this.setState({
            error: true
        })
    }


    show = () => {
        this.setState(prevstate => ({ Random: !prevstate.Random }))
        if (this.state.Random) {
            return this.name = 'show'
        }
        else {
            return this.name = 'hide'
        }

    }


    render() {
        if (this.state.error) {
            return <ErrorMessage />
        }
        const newRandomChar = this.state.Random ? <RandomChar /> : null
        const Butick = () => {

            return (
                <>
                    <Div1>
                        <Button1 onClick={this.show}>{this.name}</Button1>
                    </Div1>
                </>
            )
        }
        return (
            <>
                <Container>
                    <Header />
                </Container>
                <Container>
                    <Row>
                        <Col lg={{ size: 5, offset: 0 }}>
                            {newRandomChar}
                            <Butick onClick={this.state} />
                        </Col>
                    </Row>
                    <CharacterPage />
                    <Row>
                        <Div1/>
                        <Col lg={{ size: 5, offset: 0 }}>
                           <ItemList onCharSelected={this.onCharSelected}
                           getData={this.gotServices.getAllBooks}
                           renderItems={(item)=>item.name}
                           />
                        </Col>
                    </Row>
                    <Row>
                    <Div1/>
                        <Col lg={{ size: 5, offset: 0 }}>
                           < ItemList onCharSelected={this.onCharSelected}
                           getData={this.gotServices.getAllHouses}
                           renderItems={(item)=>item.name}
                           />
                        </Col>
                    </Row>
                </Container>
            </>
        );
    };

}
