export default class gotServices {

    constructor() {
        this._apiBase = 'https://www.anapioficeandfire.com/api'
    }
    async getResources(url) {
        const res = await fetch(`${this._apiBase}${url}`)


        if (!res.ok) {
            throw new Error(`Could not fetch ${url}, status: ${res.status}`)
        }
        return await res.json()
    }
    getAllCharacters = async () => {
        const res = await this.getResources('/characters?page=5&pageSize=10')
        console.log(res)
        return res.map(this._transFormCharacter)     
    }
    
    getCharacter = async (id) => {
        const character = await this.getResources(`/characters/${id}`)
        return this._transFormCharacter(character)
    }
    getAllHouses = async () => {
        const res = await this.getResources(`/houses/`)
        return res.map(this._transFormHouse)
    }
    getHouse = async (id) => {
        const house = await this.getResources(`/houses/${id}`)
        return this._transFormHouse(house)
    }
    getAllBooks = async (id) => {
        const Books = await  this.getResources(`/books/`)
        console.log(Books.map(this._transFormBooks))
        return Books.map(this._transFormBooks)
    }
    _transFormCharacter =  (char) => {
        return {
            name: char.name,
            gender: char.gender,
            born: char.born,
            died: char.died,
            culture: char.culture
        }
    }
    _transFormHouse =  (house) => {
        return {
            name: house.name,
            region: house.region,
            words: house.words,
            titles: house.titles,
            overload: house.overload,
            ancestralWeapons: house.ancestralWeapons
        }
    }
    _transFormBooks =  (book) => {
        return {
            name: book.name,
            numberOfPages: book.numberOfPages,
            publisher: book.publisher,
            released: book.released
        }
    }
}
