import React, { Component } from 'react'
import { Col, Row, Container } from 'reactstrap'
import ItemList from '../itemList'
import CharDetails,{Field} from '../charDetails'
import ErrorMessage from '../ErrorMessage'
import gotServices from '../../Services'
import RowBlock from '../RowBlock'


const BookList = (
    <ItemList onItemSelected={console.log("qwe")}
    getData={this.gotServices.getAllBooks}
    renderItems={(item)=>item.name}
    />
    )

export default class CharacterPage extends Component {
    state = {
        selectorChar: 130,
         charData: {},
        error: false
    }
    
    gotServices = new gotServices()

    componentDidCatch() {
        this.setState({
            error: true
        })
    }
    
    onItemSelected = (id,item) => {
        // console.log(this.state)
        this.setState({
            selectorChar: id,
            charData: item
        })
        console.log(this.state.charData)
    }

   

    render() {
        if (this.state.error) {
            return <ErrorMessage /> 
        }
        const itemList = (
            <ItemList onItemSelected={this.onItemSelected} 
            getData={this.gotServices.getAllCharacters}
            renderItems={({name,gender})=>`${name} (${gender})`}
            
          />     
        )

        const charDetails = (
            <CharDetails charId={this.state}>              
                <Field field='gender' label='Gender'/>
                <Field field='born' label='Born'/>
                <Field field='died' label='Died'/>
                <Field field='culture' label='Culture'/>
            </CharDetails>
        )
        
        return (
            <RowBlock left={itemList} right={charDetails}/>
        )
    }
}
export {BookList}
