import React, { Component } from 'react';
import { Col, Row, Container } from 'reactstrap';
import Header from '../header';
import RandomChar from '../randomChar';
import ItemList from '../itemList';
import CharDetails from '../charDetails';
import styled from 'styled-components'
import './button.css'

// import gotServices from '../../Services'
const Button1 = styled.button`
background-color: rgb(43, 97, 199);
border: none;
color: white;
padding: 2px 2px;
text-align: center;
text-decoration: none;
display: inline-block;
width: 180px;
font-size: 30px;
`;
const Div1 = styled.div`
padding-bottom:20px
width:200px`




export default class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Random: true
        }
    }
    show = () => {
        this.setState(prevstate => ({ Random: !prevstate.Random }))
        console.log('childVisible: true')
    }


    render() {
        const newRandomChar = this.state.Random ? <RandomChar /> : null
        const Butick = () => {

            return (
                <>
                    <Div1>
                        <Button1 onClick={this.show}>Hide</Button1>
                    </Div1>
                </>
            )
        }
        return (
            <>
                <Container>
                    <Header />
                </Container>
                <Container>
                    <Row>
                        <Col lg={{ size: 5, offset: 0 }}>
                            {newRandomChar}
                        </Col>
                    </Row>
                    <Butick onClick={this.state} />
                    <Row>

                        <Col md='6'>
                            <ItemList />
                        </Col>
                        <Col md='6'>
                            <CharDetails />
                        </Col>
                    </Row>
                </Container>
            </>
        );
    };

}
